using scioControlLibrary;
using scioPersistentLibrary.criterios;
using scioPersistentLibrary.interfases;
using scioSecureLibrary.dominio;
using sgmpro.dominio.configuracion;

namespace cuAbmCuenta {
    /// <summary>
    /// Esta clase hereda de CUListGenerico y se encarga de 
    /// gestionar un PanelListABMV para la entidad Cuenta.
    /// </summary>
    public class CUListCuentas : CUPreviewGenerico<Cuenta> {
        /// <summary>
        /// Constructor de la clase que inicializa elementos internos.
        /// </summary>
        public CUListCuentas() {
            ControlEditable = new CUAbmCuenta();
            ColsInvisibles.Add("Deudas");
            ColsInvisibles.Add("Gestiones");
            ColsInvisibles.Add("Convenios");
        }

        #region IControladorListable Members
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        protected override PanelListABMV<Cuenta> crearPanelListado() {
            return new PanelListCuentas(this);
        }

        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        protected override PanelPreview<Cuenta> crearPanelPreview() {
            return new PanelPreviewCuenta(ControlEditable);
        }

        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override ICriterioConsulta getFiltroMaster() {
            if (ObjetoMaster is Entidad)
                return Criterios.Igual("Entidad", ObjetoMaster);

            if (ObjetoMaster is Persona)
                return Criterios.Igual("Titular", ObjetoMaster);

            if (ObjetoMaster is Producto)
                return Criterios.Igual("Producto", ObjetoMaster);

            if (ObjetoMaster is Usuario)
                return Criterios.Igual("Gestor", ObjetoMaster);

            if (ObjetoMaster is ICriterioConsulta)
                return (ICriterioConsulta)ObjetoMaster;

            return base.getFiltroMaster();
        }
        #endregion
    }
}