using scioBaseLibrary.excepciones;
using scioControlLibrary;
using scioToolLibrary;
using sgmpro.dominio.configuracion;

namespace cuAbmEntidad {
    /// <summary>
    /// Esta clase hereda de CUAbmGenerico y se encarga de gestionar la 
    /// ventana WinABMV junto con su panelABMV para la entidad Entidad.
    /// </summary>
    public class CUAbmEntidad : CUAbmGenerico<Entidad> {
        #region IControladorEditable Members
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override PanelABMV<Entidad> crearPanelEdicion() {
            return new PanelAbmvEntidad(this);
        }

        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override void verify(params object[] parametros) {
            if (string.IsNullOrEmpty(ObjetoEnEdicion.Nombre))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("ENTIDAD-NOMBRE"));

            if (string.IsNullOrEmpty(ObjetoEnEdicion.Codigo))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("ENTIDAD-CODIGO"));

            if (string.IsNullOrEmpty(ObjetoEnEdicion.Contacto))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("ENTIDAD-PERSONA"));

            if (!Validador.ValidarEmail(ObjetoEnEdicion.Email))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("ENTIDAD-EMAIL"));

            if (!Validador.ValidarCUIT(ObjetoEnEdicion.Cuit))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("ENTIDAD-CUIT"));
        }
        #endregion
    }
}