using scioControlLibrary;
using scioPersistentLibrary.orden;
using sgmpro.dominio.configuracion;

namespace cuAbmEntidad {
    /// <summary>
    /// Esta clase hereda de CUListGenerico y se encarga de 
    /// gestionar un PanelListABMV para la entidad Entidad.
    /// </summary>
    public class CUListEntidad : CUPreviewGenerico<Entidad> {
        /// <summary>
        /// Constructor de la clase que inicializa elementos internos.
        /// </summary>
        public CUListEntidad() {
            ControlEditable = new CUAbmEntidad();

            ColsInvisibles.Add("TiposGestion");
            ColsInvisibles.Add("TiposRecibo");
            ColsInvisibles.Add("FormasPago");
            ColsInvisibles.Add("Productos");
            ColsInvisibles.Add("Tramos");
            ColsInvisibles.Add("TiposConvenio");

            Ordenamiento.Add(Orden.Asc("Nombre"));
        }

        #region IControladorListable Members
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        protected override PanelPreview<Entidad> crearPanelPreview() {
            return new PanelPreviewEntidad(ControlEditable);
        }
        #endregion
    }
}