using System.Drawing;
using cuAbmParametro.Properties;
using scioControlLibrary;
using scioParamLibrary.dominio;
using scioPersistentLibrary.orden;
using scioToolLibrary;

namespace cuAbmParametro {
    /// <summary>
    /// Esta clase hereda de CUListGenerico y se encarga de 
    /// gestionar un PanelListABMV para la entidad Parametro.
    /// </summary>
    public class CUListParametros : CUPreviewGenerico<Parametro> {
        /// <summary>
        /// Constructor de la clase que inicializa elementos internos.
        /// </summary>
        public CUListParametros() {
            ControlEditable = new CUAbmParametro();
            Ordenamiento.Add(Orden.Asc("Clave"));
            Ordenamiento.Add(Orden.Asc("Orden"));
        }

        #region IControladorPreview
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override string getTitulo() {
            return Mensaje.TextoMensaje("TITULO-ABMV-PARAMETRO");
        }

        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override Icon getIcono() {
            return Resources.icoparam;
        }
        #endregion

        #region IControladorListable Members
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        protected override PanelPreview<Parametro> crearPanelPreview() {
            return new PanelPreviewParametro(ControlEditable);
        }
        #endregion
    }
}