using scioControlLibrary;
using sgmpro.dominio.configuracion;

namespace cuAbmPersona {
    /// <summary>
    /// Esta clase hereda de CUListGenerico y se encarga de 
    /// gestionar un PanelListABMV para la entidad Persona.
    /// </summary>
    public class CUListPersonas : CUPreviewGenerico<Persona> {
        /// <summary>
        /// Constructor de la clase que inicializa elementos internos.
        /// </summary>
        public CUListPersonas() {
            ControlEditable = new CUAbmPersona();
            ColsInvisibles.Add("Contactos");
        }

        #region IControladorListable Members
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        protected override PanelPreview<Persona> crearPanelPreview() {
            return new PanelPreviewPersona(ControlEditable);
        }
        #endregion
    }
}