using scioControlLibrary;
using scioPersistentLibrary.criterios;
using scioPersistentLibrary.interfases;
using scioPersistentLibrary.orden;
using sgmpro.dominio.configuracion;

namespace cuAbmPredicado {
    /// <summary>
    /// Esta clase hereda de CUListGenerico y se encarga de 
    /// gestionar un PanelListABMV para la entidad Predicado.
    /// </summary>
    public class CUListPredicados : CUPreviewGenerico<Predicado> {
        /// <summary>
        /// Constructor de la clase que inicializa elementos internos.
        /// </summary>
        public CUListPredicados() {
            ControlEditable = new CUAbmPredicado();
            ColsInvisibles.Add("Estrategia");
            Ordenamiento.Add(Orden.Asc("NroOrden"));
        }

        #region IControladorListable Members
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        protected override PanelPreview<Predicado> crearPanelPreview() {
            return new PanelPreviewPredicado(ControlEditable);
        }

        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override ICriterioConsulta getFiltroMaster() {
            return Criterios.Igual("Estrategia", ObjetoMaster);
        }
        #endregion IControladorListable Members
    }
}