using scioBaseLibrary.excepciones;
using scioControlLibrary;
using scioToolLibrary;
using sgmpro.dominio.configuracion;

namespace cuAbmProducto {
    /// <summary>
    /// Esta clase hereda de CUAbmGenerico y se encarga de gestionar la 
    /// ventana WinABMV junto con su panelABMV para la entidad Producto.
    /// </summary>
    public class CUAbmProducto : CUAbmGenerico<Producto> {
        #region IControladorEditable Members
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override PanelABMV<Producto> crearPanelEdicion() {
            return new PanelAbmvProducto(this);
        }

        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override void add(params object[] parametros) {
            if (parametros[0] is Entidad)
                ObjetoEnEdicion = new Producto { Entidad = (Entidad)parametros[0] };
            else
                throw new AppErrorException("ERROR-ADD-WITHOUT-MASTER", "PRODUCTO");
        }

        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override void verify(params object[] parametros) {
            if (string.IsNullOrEmpty(ObjetoEnEdicion.Nombre))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("PRODUCTO-NOMBRE"));

            if (string.IsNullOrEmpty(ObjetoEnEdicion.Codigo))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("PRODUCTO-CODIGO"));

            if (string.IsNullOrEmpty(ObjetoEnEdicion.Descripcion))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("PRODUCTO-DESC"));

            if (string.IsNullOrEmpty(ObjetoEnEdicion.FormulaImputacion))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("PRODUCTO-IMPUTACION"));
        }
        #endregion
    }
}