using scioControlLibrary;
using scioPersistentLibrary.criterios;
using scioPersistentLibrary.interfases;
using scioPersistentLibrary.orden;
using sgmpro.dominio.configuracion;

namespace cuAbmTramo {
    /// <summary>
    /// Esta clase hereda de CUListGenerico y se encarga de 
    /// gestionar un PanelListABMV para la entidad Tramo.
    /// </summary>
    public class CUListTramos : CUListGenerico<Tramo> {
        /// <summary>
        /// Constructor de la clase que inicializa elementos internos.
        /// </summary>
        public CUListTramos() {
            ControlEditable = new CUAbmTramo();
            ColsInvisibles.Add("Entidad");
            Ordenamiento.Add(Orden.Asc("Limite"));
        }

        #region IControladorListable Members
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override ICriterioConsulta getFiltroMaster() {
            return Criterios.Igual("Producto", ObjetoMaster);
        }
        #endregion
    }
