using scioBaseLibrary.excepciones;
using scioControlLibrary;
using scioToolLibrary;
using sgmpro.dominio.configuracion;

namespace cuAbmVariable {
    /// <summary>
    /// Clase que hereda del ABM Genérico para la clase Variable
    /// </summary>
    public class CUAbmVariable : CUAbmGenerico<Variable> {
        #region IControladorEditable Members
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override PanelABMV<Variable> crearPanelEdicion() {
            return new PanelAbmvVariable(this);
        }

        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override void verify(params object[] parametros) {
            if (string.IsNullOrEmpty(ObjetoEnEdicion.Descripcion))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("VARIABLE-DESC"));

            if (string.IsNullOrEmpty(ObjetoEnEdicion.Nombre))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("VARIABLE-NOMBRE"));

            if (string.IsNullOrEmpty(ObjetoEnEdicion.Valor))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("VARIABLE-VALOR"));
        }
        #endregion
    }
}