using scioBaseLibrary.excepciones;
using scioControlLibrary;
using scioSecureLibrary.dominio;
using scioToolLibrary;

namespace cuConfigurarUsuarios {
    /// <summary>
    /// Esta clase hereda de CUAbmGenerico y se encarga de gestionar la 
    /// ventana WinABMV junto con su panelABMV para la entidad Usuario.
    /// </summary>
    public class CUAbmUsuario : CUAbmGenerico<Usuario> {
        #region IControladorEditable Members
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override PanelABMV<Usuario> crearPanelEdicion() {
            return new PanelAbmvUsuario(this);
        }

        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override void verify(params object[] parametros) {
            if (string.IsNullOrEmpty(ObjetoEnEdicion.Nombre))
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("USUARIO-NOMBRE"));

            if (ObjetoEnEdicion.Legajo <= 0)
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("USUARIO-LEGAJO"));

            if (string.IsNullOrEmpty(ObjetoEnEdicion.Password) || ObjetoEnEdicion.Password.Trim().Length < 4)
                throw new DataErrorException("CAMPO-NOK", Mensaje.TextoValidacion("USUARIO-PASSWORD"));
        }
        #endregion
    }
}