using scioControlLibrary;
using scioPersistentLibrary.orden;
using scioSecureLibrary.dominio;

namespace cuConfigurarUsuarios {
    /// <summary>
    /// Esta clase hereda de CUListGenerico y se encarga de 
    /// gestionar un PanelListABMV para la entidad Perfil.
    /// </summary>
    public class CUListPerfiles : CUPreviewGenerico<Perfil> {
        /// <summary>
        /// Constructor de la clase que inicializa elementos internos.
        /// </summary>
        public CUListPerfiles() {
            ControlEditable = new CUAbmPerfil();
            ColsInvisibles.Add("Permisos");
            ColsInvisibles.Add("Perfiles");
            Ordenamiento.Add(Orden.Asc("Nombre"));
        }

        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        protected override PanelPreview<Perfil> crearPanelPreview() {
            return new PanelPreviewPerfil(ControlEditable);
        }
    }
