using scioControlLibrary;
using scioPersistentLibrary.orden;
using scioSecureLibrary.dominio;

namespace cuConfigurarUsuarios {
    /// <summary>
    /// Esta clase hereda de CUListGenerico y se encarga de 
    /// gestionar un PanelListABMV para la entidad Usuario.
    /// </summary>
    public class CUListUsuarios : CUPreviewGenerico<Usuario> {
        /// <summary>
        /// Constructor de la clase que inicializa elementos internos.
        /// </summary>
        public CUListUsuarios() {
            ControlEditable = new CUAbmUsuario();
            ColsInvisibles.Add("Password");
            ColsInvisibles.Add("Perfiles");
            ColsInvisibles.Add("Roles");
            Ordenamiento.Add(Orden.Asc("Nombre"));
        }

        #region IControladorListable Members
        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        protected override PanelPreview<Usuario> crearPanelPreview() {
            return new PanelPreviewUsuario(ControlEditable);
        }
        #endregion
    }
}