using scioControlLibrary;
using scioPersistentLibrary.criterios;
using scioPersistentLibrary.interfases;
using scioPersistentLibrary.orden;
using sgmpro.dominio.scheduler;

namespace cuGenerarJobs {
    /// <summary>
    ///   Esta clase hereda de CUListGenerico y se encarga de 
    ///   gestionar un PanelListABMV para la entidad Ejecucion.
    /// </summary>
    public class CUListEjecuciones : CUListGenerico<Ejecucion> {
        /// <summary>
        ///   Constructor de la Direction que inicializa elementos internos.
        /// </summary>
        public CUListEjecuciones() {
            ControlEditable = new CUAbmEjecucion();
            ColsInvisibles.Add("Job");
            Ordenamiento.Add(Orden.Desc("Inicio"));
        }


        /// <summary>
        /// Implementación del método de la interfaz.
        /// </summary>
        public override ICriterioConsulta getFiltroMaster() {
            return Criterios.Igual("Job", ObjetoMaster);
        }
    }
