using scioToolLibrary.enums;
using scioToolLibrary.excepciones;

namespace scioBaseLibrary.excepciones {
    /// <summary>
    /// Esta clase de excepción es la base de las excepciones del 'framework'
    /// y debe utilizarse como clase base para representar todas las excepciones
    /// asociados a un problema dentro de un sistema generado con el mismo.
    /// </summary>
    public class SistemaErrorException : ScioErrorException {
        public SistemaErrorException() : this("EXCEPTION", "...") {}
        public SistemaErrorException(string mensaje) : this(mensaje, "...") {}
        public SistemaErrorException(string mensaje, string extra) : base(mensaje, extra) {
            Sistema.Controlador.logear(mensaje, ENivelMensaje.ERROR, null);
        }
    }
}