using System.Collections.Generic;
using scioPersistentLibrary.acceso;
using scioPersistentLibrary.criterios;

namespace scioControlLibrary.dominio.repos {
    /// <summary>
    /// Clase que persiste los datos de nodos de un Tree (NodeTree)
    /// </summary>
    public class NodoTrees : RepositorioPersistente<NodoTree> {
        /// <summary>
        /// Devuelve los nodos que se encuentren en el nivel solicitado.
        /// </summary>
        /// <param name="unNivel">
        /// El nivel para el cual se desean obtener los nodos.
        /// </param>
        /// <returns>
        /// La lista de nodos que poseen el nivel deseado.
        /// </returns>
        public static IList<NodoTree> GetNodoByNivel(int unNivel) {
            return GetByCriteria(
                true,
                new[] {Criterios.Igual("Nivel", unNivel)},
                null);
        }

        /// <summary>
        /// Devuelve un nodo que se encuentren en el nivel 0 (raiz)
        /// y que tiene una descripción particular (que definiría 
        /// que árbol contiene).
        /// </summary>
        /// <param name="desc">
        /// La descripción para el nodo raíz buscado.
        /// </param>
        /// <returns>
        /// El nodo raiz del árbol deseado (según su descripción).
        /// </returns>
        public static NodoTree GetRaizByDescripcion(string desc) {
            return GetUniqueByCriteria(
                true,
                new[] {Criterios.Igual("Nivel", 0), Criterios.Igual("Descripcion", desc)});
        }
    }
}