using NHibernate.Criterion;
using scioPersistentLibrary.interfases;

namespace scioPersistentLibrary.criterios {
    /// <summary>
    /// Esta clase abstracta representa el concepto de una expresión lógica 
    /// NOT dentro de un criterio de consulta a un motor de persistencia.  
    /// </summary>
    public class ExpresionNot : ExpresionLogica {
        /// <summary>
        /// Constructor de la clase que únicamente pasa los argumentos a la clase base.
        /// </summary>
        public ExpresionNot(ICriterioConsulta operando) : base(operando, null) {}

        /// <summary>
        /// Método sobrecargado (ver en clase base).
        /// </summary>
        protected override string getOpString() {
            return "not";
        }

        /// <summary>
        /// Método sobrecargado (ver en clase base).
        /// </summary>
        protected override ICriterion getCriterionNH(ICriterioConsulta left, ICriterioConsulta rigth) {
            return Restrictions.Not(left.getCriterioNH());
        }
    }
}