using System;

namespace scioToolLibrary.excepciones {
    /// <summary>
    /// Esta clase de excepción debe utilizarse para representar todos
    /// los errores asociados a un problema de acceso, ó permiso a un 
    /// recurso ó una acción determinada.
    /// </summary>
    public class ScioErrorException : ApplicationException {
        protected readonly string _extra;

        public ScioErrorException() : this("EXCEPCION", "...") {}

        public ScioErrorException(string mensaje) : this(mensaje, "...") {}

        public ScioErrorException(string mensaje, string extra) : base(mensaje) {
            _extra = extra;
        }

        public override string ToString() {
            return string.Format("{0}\r\n{1}", Mensaje.TextoMensaje(Message), _extra);
        }
    }
}