using System;
using System.Collections.Generic;

namespace sgmpro.enums {
    /// <summary>
    /// Calse que representa una Enumeracion de los criterios 
    /// de comparacion de Predicados
    /// </summary>
    public class ECriterioComparacion {
        public const string DISTINTO = " <> ";
        public const string IGUAL = " == ";
        public const string MAYOR = " > ";
        public const string MAYORIGUAL = " >= ";
        public const string MENOR = " < ";
        public const string MENORIGUAL = " <= ";

        /// <summary>
        /// Devuelve la lista de todos los criterios.
        /// Se utiliza para cargar combos
        /// </summary>
        /// <returns></returns>
        public static IList<String> GetTodos() {
            return new List<String> {MAYOR, MAYORIGUAL, IGUAL, MENORIGUAL, MENOR, DISTINTO};
        }
    }
}